import React, { useState, useEffect } from 'react';
import { fetchWithAuth } from '../utils/api';

const MouseButtonHeatmap = ({ deviceId }) => {
  const [buttonData, setButtonData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    const fetchButtonData = async () => {
      if (!deviceId) return;
      
      try {
        const token = localStorage.getItem('authToken');
        const response = await fetch(`http://localhost:5000/api/metrics/mouse_button_usage/${deviceId}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch mouse button usage data');
        }

        const data = await response.json();
        setButtonData(data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching mouse button data:', err);
        setError(err.message);
        // Generate fallback data for demonstration
        generateFallbackData();
      }
    };
    
    const generateFallbackData = () => {
      // Create realistic fallback data showing button usage for a gaming mouse
      const fallbackData = {
        button_usage: {
          'left_click': 68,      // Primary button - highest usage
          'right_click': 42,     // Secondary button
          'middle_click': 15,    // Middle button/scroll wheel
          'button4': 28,         // Side button 1 (forward)
          'button5': 34,         // Side button 2 (back)
          'dpi_button': 7,       // DPI switcher
          'button7': 14,         // Additional button
          'button8': 18          // Additional button
        },
        pressure_data: {
          'left_click': 92,      // Higher pressure indicating potential strain
          'right_click': 85,     
          'middle_click': 70,    
          'button4': 77,         
          'button5': 80,         
          'dpi_button': 60,
          'button7': 65,
          'button8': 68
        },
        click_frequency: {
          'left_click': {
            'hourly': 3200,
            'average_interval_ms': 450
          },
          'right_click': {
            'hourly': 1800,
            'average_interval_ms': 900
          }
        },
        timestamp: new Date().toISOString()
      };
      
      setButtonData(fallbackData);
      setLoading(false);
    };
    
    fetchButtonData();
    
    // Refresh data periodically
    const interval = setInterval(fetchButtonData, 10000);
    return () => clearInterval(interval);
  }, [deviceId]);
  
  if (loading) {
    return (
      <div className="card">
        <h2 className="card-title">Mouse Button Usage Analysis</h2>
        <p>Loading button usage data...</p>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="card">
        <h2 className="card-title">Mouse Button Usage Analysis</h2>
        <p className="error">Error: {error}</p>
      </div>
    );
  }
  
  // Calculate max usage for normalized display
  const maxUsage = Math.max(...Object.values(buttonData.button_usage));
  const maxPressure = Math.max(...Object.values(buttonData.pressure_data));
  
  // Helper function to get color based on pressure value
  const getPressureColor = (value) => {
    // Scale from green (low) to red (high)
    if (value < 60) return '#4caf50'; // Low pressure - safe
    if (value < 80) return '#ff9800'; // Medium pressure - warning
    return '#f44336';                 // High pressure - danger
  };
  
  return (
    <div className="card">
      <h2 className="card-title">Mouse Button Usage Analysis</h2>
      <p>Ergonomic analysis of button usage to help prevent hand strain and RSI</p>
      
      <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
        {/* Mouse visualization */}
        <div style={{ 
          position: 'relative',
          width: '320px', 
          height: '180px',
          margin: '20px auto',
          backgroundColor: '#2a2a2a',
          borderRadius: '60px 60px 120px 120px',
          boxShadow: '0 10px 15px rgba(0,0,0,0.2)',
          overflow: 'visible'
        }}>
          {/* Left button */}
          <div
            style={{ 
              position: 'absolute',
              top: '0',
              left: '0',
              width: '160px',
              height: '80px',
              borderRadius: '60px 0 0 0',
              background: `rgba(255, 0, 0, ${buttonData.button_usage.left_click / maxUsage * 0.7})`,
              border: `2px solid ${getPressureColor(buttonData.pressure_data.left_click)}`,
              boxSizing: 'border-box',
              cursor: 'pointer'
            }}
            title={`Left Click: ${buttonData.button_usage.left_click} clicks (${buttonData.pressure_data.left_click}% pressure)`}
          />
          
          {/* Right button */}
          <div
            style={{ 
              position: 'absolute',
              top: '0',
              right: '0',
              width: '160px',
              height: '80px',
              borderRadius: '0 60px 0 0',
              background: `rgba(255, 0, 0, ${buttonData.button_usage.right_click / maxUsage * 0.7})`,
              border: `2px solid ${getPressureColor(buttonData.pressure_data.right_click)}`,
              boxSizing: 'border-box',
              cursor: 'pointer'
            }}
            title={`Right Click: ${buttonData.button_usage.right_click} clicks (${buttonData.pressure_data.right_click}% pressure)`}
          />
          
          {/* Scroll wheel / middle button */}
          <div
            style={{ 
              position: 'absolute',
              top: '30px',
              left: '150px',
              width: '20px',
              height: '30px',
              borderRadius: '10px',
              background: `rgba(255, 0, 0, ${buttonData.button_usage.middle_click / maxUsage * 0.7})`,
              border: `2px solid ${getPressureColor(buttonData.pressure_data.middle_click)}`,
              boxSizing: 'border-box',
              cursor: 'pointer'
            }}
            title={`Scroll Wheel: ${buttonData.button_usage.middle_click} clicks (${buttonData.pressure_data.middle_click}% pressure)`}
          />
          
          {/* DPI button */}
          <div
            style={{ 
              position: 'absolute',
              top: '70px',
              left: '150px',
              width: '20px',
              height: '20px',
              borderRadius: '10px',
              background: `rgba(255, 0, 0, ${buttonData.button_usage.dpi_button / maxUsage * 0.7})`,
              border: `2px solid ${getPressureColor(buttonData.pressure_data.dpi_button)}`,
              boxSizing: 'border-box',
              cursor: 'pointer'
            }}
            title={`DPI Button: ${buttonData.button_usage.dpi_button} clicks (${buttonData.pressure_data.dpi_button}% pressure)`}
          />
          
          {/* Side button 1 */}
          <div
            style={{ 
              position: 'absolute',
              top: '50px',
              left: '-5px',
              width: '10px',
              height: '25px',
              borderRadius: '5px 0 0 5px',
              background: `rgba(255, 0, 0, ${buttonData.button_usage.button4 / maxUsage * 0.7})`,
              border: `2px solid ${getPressureColor(buttonData.pressure_data.button4)}`,
              boxSizing: 'border-box',
              cursor: 'pointer'
            }}
            title={`Side Button 1: ${buttonData.button_usage.button4} clicks (${buttonData.pressure_data.button4}% pressure)`}
          />
          
          {/* Side button 2 */}
          <div
            style={{ 
              position: 'absolute',
              top: '85px',
              left: '-5px',
              width: '10px',
              height: '25px',
              borderRadius: '5px 0 0 5px',
              background: `rgba(255, 0, 0, ${buttonData.button_usage.button5 / maxUsage * 0.7})`,
              border: `2px solid ${getPressureColor(buttonData.pressure_data.button5)}`,
              boxSizing: 'border-box',
              cursor: 'pointer'
            }}
            title={`Side Button 2: ${buttonData.button_usage.button5} clicks (${buttonData.pressure_data.button5}% pressure)`}
          />
        </div>
        
        {/* Usage statistics and ergonomic recommendations */}
        <div style={{ marginTop: '20px' }}>
          <h3>Button Usage Statistics</h3>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
            {Object.entries(buttonData.button_usage).map(([button, usage]) => (
              <div key={button} style={{ 
                flex: '1', 
                minWidth: '120px',
                padding: '10px',
                backgroundColor: 'var(--card-bg)', 
                borderRadius: '8px',
                border: `1px solid ${getPressureColor(buttonData.pressure_data[button])}`
              }}>
                <h4 style={{ margin: '0 0 5px 0' }}>{button.replace('_', ' ').toUpperCase()}</h4>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <span>Usage:</span>
                  <span>{usage}%</span>
                </div>
                <div style={{ 
                  height: '10px', 
                  backgroundColor: 'var(--bg-color)', 
                  borderRadius: '5px',
                  margin: '5px 0',
                  overflow: 'hidden'
                }}>
                  <div style={{ 
                    height: '100%', 
                    width: `${(usage / maxUsage) * 100}%`,
                    backgroundColor: getPressureColor(buttonData.pressure_data[button]),
                    borderRadius: '5px'
                  }} />
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <span>Pressure:</span>
                  <span>{buttonData.pressure_data[button]}%</span>
                </div>
              </div>
            ))}
          </div>
        </div>
        
        {/* Ergonomic recommendations based on the data */}
        <div style={{ 
          marginTop: '20px',
          padding: '15px',
          backgroundColor: 'rgba(98, 0, 234, 0.1)',
          borderRadius: '8px',
          border: '1px solid var(--primary-color)'
        }}>
          <h3 style={{ margin: '0 0 10px 0' }}>Ergonomic Analysis & Recommendations</h3>
          <ul style={{ margin: '0', paddingLeft: '20px' }}>
            {buttonData.pressure_data.left_click > 90 && (
              <li>High pressure detected on left click button. Consider reducing force when clicking to prevent repetitive strain injury.</li>
            )}
            {buttonData.pressure_data.right_click > 90 && (
              <li>High pressure detected on right click button. Try relaxing your finger and using less force.</li>
            )}
            {buttonData.button_usage.left_click > 65 && (
              <li>Heavy reliance on left click detected. Consider binding some actions to other buttons to distribute strain.</li>
            )}
            {buttonData.button_usage.button4 > 25 && buttonData.button_usage.button5 > 25 && (
              <li>High usage of side buttons may cause thumb strain. Take regular breaks and do hand stretches.</li>
            )}
            {buttonData.click_frequency.left_click.hourly > 3000 && (
              <li>Very high click rate detected. Consider using macros for common actions to reduce click frequency.</li>
            )}
            <li>Remember to take a 5-minute break every 25 minutes of intensive mouse usage.</li>
            <li>Perform hand stretches and exercises regularly to prevent RSI (Repetitive Strain Injury).</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default MouseButtonHeatmap;
